INSERT INTO `bcv_medical_questions` (`bcv_id`, `bcv_code`, `bcv_question`) VALUES
(1, 'A1b', 'Trong 6 tháng qua, trọng lượng của BMBH/ NĐBH có thay đổi quá 5kg hay không?'),
(2, 'A2a', 'NDBH đã từng hút thuốc lá với số lượng trên 10 điếu một ngày không?'),
(3, 'A2b', 'NDBH có uống bia, rượu với số lượng bình quân trên 2500ml bia hay 250ml rượu trong một tuần không?'),
(4, 'A2c', 'NDBH có đang được điều trị bằng bất kỳ loại thuốc nào không?'),
(5, 'A2d', 'NDBH đã bao giờ sử dụng chất gây nghiện, chất kích thích hoặc ma tuý không?'),
(6, 'A3a', 'Tăng cholesterol, huyết áp cao, huyết áp thấp, đột quỵ, cơn đau tim, âm thổi ở tim, khó thở, rối loạn nhịp tim, thấp tim, đau ngực, bệnh van tim, bệnh hoặc bất kỳ rối loạn ở tim hoặc mạch máu?'),
(7, 'A3b', 'Bệnh suyễn, viêm phế quản, viêm phổi, lao phổi, ho kéo dài, ho ra máu hoặc những bệnh hô hấp khác? c) Bệnh tiểu đường, bệnh tuyến giáp, hoặc rối loạn nội tiết khác? d) Suy sụp thần kinh, động kinh, mấ'),
(8, 'A3e', 'Chảy dịch tai, chảy máu mũi, thị lực kém, rối loạn thính lực, rối loạn ngôn ngữ hoặc bệnh khác ở tai, mắt, mũi và họng?'),
(9, 'A3f', 'Bệnh sốt rét, bệnh sốt của miền nhiệt đới, bệnh sốt cơn hoặc sốt kéo dài?'),
(10, 'A3g', 'Loét dạ dày hoặc tá tràng, tiêu chảy kéo dài, tiêu chảy mãn tính hoặc từng đợt, sỏi mật, viêm gan hoặc rối loạn chức năng gan, vàng da, thương hàn, viêm đại tràng, đi cầu ra máu hoặc bệnh đường tiêu h'),
(11, 'A3h', 'Máu, đạm hoặc đường trong nước tiểu, sỏi thân, viêm hoặc các bệnh khác ở thận, đường tiết niệu hoặc cơ quan sinh dục?'),
(12, 'A3i', 'NDBH có bao giờ được chẩn đoán và điều trị bệnh lây lan qua đường sinh dục, Hội chứng suy giảm miễn dịch mắc phải (HIV/AIDS)?'),
(13, 'A3j', 'NDBH có bao giờ làm xét nghiệm HIV không?'),
(14, 'A3k', 'Bệnh thiếu máu, bệnh bạch huyết, suy tủy, xuất huyết, hoặc những bất thường về máu?'),
(15, 'A3l', 'Bệnh viêm khớp, bệnh hoặc biến dạng hoặc rối loạn ở cơ, da, xương, khớp (chi, cột sống,...)?'),
(16, 'A3m', 'Bệnh ung thư, u bướu hoặc phì đại ở tuyến: tuyến vú, hạch lymphô,...?'),
(17, 'A3n', 'Bệnh tự miễm: bệnh xơ cứng rải rác, lupút ban đỏ,... ?'),
(18, 'A3o', 'Bất kỳ bệnh, rối loạn, phẫu thuật, mất khả năng hoạt động, hoặc tổn thương khác không?'),
(19, 'A4a', 'Khám sức khoẻ hoặc điều trị nội hay ngoại trú tại bệnh viện hoặc trung tâm y tế nào không?'),
(20, 'A4b', 'Được chụp X-quang, Điện tâm đồ, Điện tâm đồ gắng sức, Siêu âm, Chụp cắt lớp điện toán hoặc sinh thiết, Chụp nhũ ảnh, Xét nghiệm máu, Xét nghiệm nước tiểu không?'),
(21, 'A4c', 'Có biểu hiện triệu chứng nào sau đây kéo dài hơn một tuần: Mệt mỏi sụt cân, tiêu chảy, phì đại hạch hoặc sang thương bất thường ở da?'),
(22, 'A5', 'NDBH có dị tật nào không? Bẩm sinh hay mắc phải?'),
(23, 'A6', 'NDBH có bị tai nạn bao giờ không? Sau đó để lại thương tật nào không?'),
(24, 'A7a', 'NDBH có đang mang thai?'),
(25, 'A7b', 'NDBH có bị biến chứng trong các lần thai sản trước không?'),
(26, 'A8', 'NDBH có tham gia hoặc có ý định tham gia vào các môn thể thao, trò giải trí hoặc công việc có tính nguy hiểm không?'),
(27, 'A9', 'NDBH có thường xuyên đi lại bằng đường thuỷ không?'),
(28, 'A10', 'Ngoài yêu cầu bảo hiểm này, NDBH còn yêu cầu/Hợp đồng bảo hiểm nào khác với Dai-ichi Việt Nam hoặc với bất kỳ Công ty Bảo hiểm nhân thọ nào khác với những thỏa thuận đặc biệt không?'),
(29, 'A11', 'Xin cho biết Cha Mẹ hoặc anh chị em của NDBH (dưới 55 tuổi) có ai đã qua đời hoặc mắc bệnh ung thư, tiểu đường, tim mạch, đột qụy, cao huyết áp, bệnh thận, bệnh về máu, sa sút trí tuệ, bệnh alzheimer,'),
(30, 'B1', 'NDBH có bị thương tổn hay khuyết tật lúc chào đời không? NDBH có chịu những bất thường khi sinh, bị tật bẩm sinh "Xanh tím trẻ sơ sinh", mất nhận thức hoặc hội chứng Down không?'),
(31, 'B2', 'NDBH được sanh non hoặc sanh già tháng không?'),
(32, 'B3', 'NDBH cân nặng lúc sinh dưới 2,5 kg?'),
(33, 'B4', 'Sau khi sinh, NDBH ở bệnh viện trên 7 ngày?'),
(34, 'B5', 'NĐBH có bỏ sót mũi tiêm nào so với chương trình tiêm chủng quốc gia không?');
